import React, { useState } from 'react';
import axios from 'axios';  // Import axios for API requests
import '../App.css';

const EditQuestion = () => {
    const [question, setQuestion] = useState('');
    const [options, setOptions] = useState(['', '', '']);
    const [mark, setMark] = useState('');
    const [negativeMark, setNegativeMark] = useState('');
    const [loading, setLoading] = useState(false);  // To show loading state
    const [error, setError] = useState(null);  // For error handling
    const [successMessage, setSuccessMessage] = useState('');  // For success message after submitting

    const handleOptionChange = (index, value) => {
        const newOptions = [...options];
        newOptions[index] = value;
        setOptions(newOptions);
    };

    const addOption = () => {
        setOptions([...options, '']);
    };

    const handleSubmit = async () => {
        setLoading(true);  // Show loading indicator

        try {
            // Send data to the backend using a POST request
            const response = await axios.post('http://localhost:8000/staff/addquestion/', {
                question,
                options,
                mark,
                negativeMark
            });

            // Handle the response from the backend
            console.log(response.data);  // Log success message

            if (response.status === 200) {
                setSuccessMessage('Question added successfully!');
                setError(null);  // Clear any previous errors
            }
        } catch (err) {
            console.error('Error submitting question:', err);
            setError('Failed to add question. Please try again.');
            setSuccessMessage('');  // Clear success message on error
        } finally {
            setLoading(false);  // Hide loading indicator after request
        }
    };

    return (
        <div className="edit-question-container">
            <h1>Add Question</h1>
            <div className="form-group">
                <label>Question*</label>
                <textarea
                    value={question}
                    onChange={(e) => setQuestion(e.target.value)}
                    placeholder="Enter your question here"
                ></textarea>
            </div>

            <div className="form-group">
                <label>Answer*</label>
                <p>Add choices & choose the correct answers</p>
                {options.map((option, index) => (
                    <input
                        key={index}
                        type="text"
                        value={option}
                        onChange={(e) => handleOptionChange(index, e.target.value)}
                        placeholder={`Option ${index + 1}`}
                    />
                ))}
                <button onClick={addOption} className="add-option-btn">Add Option</button>
            </div>

            <div className="form-group">
                <label>Mark</label>
                <input
                    type="text"
                    value={mark}
                    onChange={(e) => setMark(e.target.value)}
                    placeholder="Enter mark for the question"
                />
            </div>

            <div className="form-group">
                <label>Negative Mark</label>
                <input
                    type="text"
                    value={negativeMark}
                    onChange={(e) => setNegativeMark(e.target.value)}
                    placeholder="Enter negative mark"
                />
            </div>

            {loading && <div className="loading">Submitting...</div>} {/* Show loading indicator */}
            {successMessage && <div className="success-message">{successMessage}</div>} {/* Success message */}
            {error && <div className="error">{error}</div>} {/* Error message */}

            <button onClick={handleSubmit} className="submit-btn">Next Step</button>
        </div>
    );
};

export default EditQuestion;
