import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const AddQuestion = () => {
    const [question, setQuestion] = useState('');
    const [options, setOptions] = useState(['', '', '']);
    const [correctOption, setCorrectOption] = useState('');
    const [mark, setMark] = useState('');
    const [negativeMark, setNegativeMark] = useState('');
    const [loading, setLoading] = useState(false);
    const [successMessage, setSuccessMessage] = useState('');
    const [error, setError] = useState('');

    const navigate = useNavigate();

    // Handle option changes
    const handleOptionChange = (index, value) => {
        const newOptions = [...options];
        newOptions[index] = value;
        setOptions(newOptions);
    };

    // Add new option field
    const addOption = () => {
        setOptions([...options, '']);
    };

    // Submit question data
    const handleSubmit = async () => {
        setLoading(true);
        try {
            const response = await axios.post('http://localhost:8000/staff/addquestion/', {
                question,
                options,
                correctOption,
                mark,
                negativeMark,
            });

            if (response.status === 200) {
                setSuccessMessage('Question added successfully!');
                setError(null); // Clear any previous error
                
                // Save the question in session storage
                const savedQuestions = JSON.parse(sessionStorage.getItem('questions')) || [];
                savedQuestions.push({
                    question,
                    options,
                    correctOption,
                    mark,
                    negativeMark,
                });
                sessionStorage.setItem('questions', JSON.stringify(savedQuestions));

                // Redirect to the StaffHome page after submission
                navigate('/addquestion');
            }
        } catch (err) {
            setError(err.response?.data?.error || 'Failed to add question. Please try again.');
            setSuccessMessage('');
        } finally {
            setLoading(false);
        }
    };

    // Reset form fields for adding a new question
    const handleNext = () => {
        setQuestion('');
        setOptions(['', '', '']);
        setCorrectOption('');
        setMark('');
        setNegativeMark('');
        setSuccessMessage(''); // Clear success message
        setError(''); // Clear error message
    };


    <button
    style={{ ...styles.button, ...styles.secondaryButton }}
    onClick={() => navigate('/staffhome')}  // Change from navigate(-1) to navigate('/staffhome')
>
    Back
</button>


    // Inline CSS styles
    const styles = {
        container: {
            maxWidth: '600px',
            margin: '50px auto',
            padding: '20px',
            borderRadius: '8px',
            backgroundColor: '#f9f9f9',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        },
        heading: {
            fontSize: '24px',
            marginBottom: '20px',
            textAlign: 'center',
            color: '#333',
        },
        formGroup: {
            marginBottom: '15px',
        },
        label: {
            display: 'block',
            marginBottom: '8px',
            fontWeight: 'bold',
            color: '#555',
        },
        input: {
            width: '100%',
            padding: '10px',
            borderRadius: '4px',
            border: '1px solid #ccc',
            marginBottom: '10px',
        },
        textarea: {
            width: '100%',
            padding: '10px',
            borderRadius: '4px',
            border: '1px solid #ccc',
            resize: 'vertical',
        },
        button: {
            padding: '10px 20px',
            borderRadius: '4px',
            border: 'none',
            cursor: 'pointer',
            marginRight: '10px',
        },
        primaryButton: {
            backgroundColor: '#4CAF50',
            color: '#fff',
        },
        secondaryButton: {
            backgroundColor: '#f44336',
            color: '#fff',
        },
        message: {
            padding: '10px',
            borderRadius: '4px',
            marginBottom: '10px',
            textAlign: 'center',
        },
        successMessage: {
            backgroundColor: '#dff0d8',
            color: '#3c763d',
        },
        errorMessage: {
            backgroundColor: '#f2dede',
            color: '#a94442',
        },
    };

    return (
        <div style={styles.container}>
            <h1 style={styles.heading}>Add Question</h1>
            <div style={styles.formGroup}>
                <label style={styles.label}>Question*</label>
                <textarea
                    style={styles.textarea}
                    value={question}
                    onChange={(e) => setQuestion(e.target.value)}
                    placeholder="Enter your question here"
                />
            </div>

            <div style={styles.formGroup}>
                <label style={styles.label}>Options*</label>
                {options.map((option, index) => (
                    <input
                        key={index}
                        style={styles.input}
                        type="text"
                        value={option}
                        onChange={(e) => handleOptionChange(index, e.target.value)}
                        placeholder={`Option ${index + 1}`}
                    />
                ))}
                <button style={{ ...styles.button, ...styles.primaryButton }} onClick={addOption}>
                    Add Option
                </button>
            </div>

            <div style={styles.formGroup}>
                <label style={styles.label}>Correct Option*</label>
                <input
                    style={styles.input}
                    type="text"
                    value={correctOption}
                    onChange={(e) => setCorrectOption(e.target.value)}
                    placeholder="Enter the correct option"
                />
            </div>

            <div style={styles.formGroup}>
                <label style={styles.label}>Mark*</label>
                <input
                    style={styles.input}
                    type="text"
                    value={mark}
                    onChange={(e) => setMark(e.target.value)}
                    placeholder="Enter marks for the question"
                />
            </div>

            <div style={styles.formGroup}>
                <label style={styles.label}>Negative Mark*</label>
                <input
                    style={styles.input}
                    type="text"
                    value={negativeMark}
                    onChange={(e) => setNegativeMark(e.target.value)}
                    placeholder="Enter negative marks for the question"
                />
            </div>

            {loading && <p>Submitting...</p>}
            {successMessage && <p style={{ ...styles.message, ...styles.successMessage }}>{successMessage}</p>}
            {error && <p style={{ ...styles.message, ...styles.errorMessage }}>{error}</p>}

            <div style={styles.formActions}>
                <button
                    style={{ ...styles.button, ...styles.primaryButton }}
                    onClick={handleSubmit}
                >
                    Submit
                </button>
                <button
                    style={{ ...styles.button, ...styles.primaryButton }}
                    onClick={handleNext}
                >
                    Next
                </button>
                <button
                    style={{ ...styles.button, ...styles.secondaryButton }}
                    onClick={() => navigate(-1)}
                >
                    Back
                </button>
            </div>
        </div>
    );
};

export default AddQuestion;
