import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const AddQuestion = () => {
    const [question, setQuestion] = useState('');
    const [options, setOptions] = useState(['', '', '']);
    const [correctOption, setCorrectOption] = useState('');
    const [mark, setMark] = useState('');
    const [negativeMark, setNegativeMark] = useState('');
    const [loading, setLoading] = useState(false);
    const [successMessage, setSuccessMessage] = useState('');
    const [error, setError] = useState('');
    const [questionsList, setQuestionsList] = useState([]);
    const [activeQuestion, setActiveQuestion] = useState(null);

    const navigate = useNavigate();

    // Handle option changes
    const handleOptionChange = (index, value) => {
        const newOptions = [...options];
        newOptions[index] = value;
        setOptions(newOptions);
    };

    // Add new option field
    const addOption = () => {
        setOptions([...options, '']);
    };

    // Submit question data
    const handleSubmit = async () => {
        setLoading(true);
        try {
            const response = await axios.post('http://localhost:8000/staff/addquestion/', {
                question,
                options,
                correctOption,
                mark,
                negativeMark,
            });

            if (response.status === 200) {
                setSuccessMessage('Question added successfully!');
                setError(null); // Clear any previous error
                
                // Save the question in session storage
                const savedQuestions = JSON.parse(sessionStorage.getItem('questions')) || [];
                const newQuestion = {
                    question,
                    options,
                    correctOption,
                    mark,
                    negativeMark,
                };
                savedQuestions.push(newQuestion);
                sessionStorage.setItem('questions', JSON.stringify(savedQuestions));

                // Update the questions list to display the newly added question
                setQuestionsList(savedQuestions);

                // Optionally, redirect or display success message
            }
        } catch (err) {
            setError(err.response?.data?.error || 'Failed to add question. Please try again.');
            setSuccessMessage('');
            // Optional: Clear session storage on error if needed
            sessionStorage.removeItem('questions');
        } finally {
            setLoading(false);
        }
    };

    // Handle grid tile click to show the selected question's details
    const handleGridTileClick = (index) => {
        setActiveQuestion(questionsList[index]);
    };

    // Reset form fields for adding a new question
    const handleNext = () => {
        setQuestion('');
        setOptions(['', '', '']);
        setCorrectOption('');
        setMark('');
        setNegativeMark('');
        setSuccessMessage(''); // Clear success message
        setError(''); // Clear error message
    };

    // Inline CSS styles
    const styles = {
        container: {
            maxWidth: '600px',
            margin: '50px auto',
            padding: '20px',
            borderRadius: '8px',
            backgroundColor: '#f9f9f9',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        },
        heading: {
            fontSize: '24px',
            marginBottom: '20px',
            textAlign: 'center',
            color: '#333',
        },
        formGroup: {
            marginBottom: '15px',
        },
        label: {
            display: 'block',
            marginBottom: '8px',
            fontWeight: 'bold',
            color: '#555',
        },
        input: {
            width: '100%',
            padding: '10px',
            borderRadius: '4px',
            border: '1px solid #ccc',
            marginBottom: '10px',
        },
        textarea: {
            width: '100%',
            padding: '10px',
            borderRadius: '4px',
            border: '1px solid #ccc',
            resize: 'vertical',
        },
        button: {
            padding: '10px 20px',
            borderRadius: '4px',
            border: 'none',
            cursor: 'pointer',
            marginRight: '10px',
        },
        primaryButton: {
            backgroundColor: '#4CAF50',
            color: '#fff',
        },
        secondaryButton: {
            backgroundColor: '#f44336',
            color: '#fff',
        },
        message: {
            padding: '10px',
            borderRadius: '4px',
            marginBottom: '10px',
            textAlign: 'center',
        },
        successMessage: {
            backgroundColor: '#dff0d8',
            color: '#3c763d',
        },
        errorMessage: {
            backgroundColor: '#f2dede',
            color: '#a94442',
        },
        gridContainer: {
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill, minmax(80px, 1fr))',
            gap: '10px',
            marginTop: '20px',
        },
        gridTile: {
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            padding: '10px',
            backgroundColor: '#f2f2f2',
            borderRadius: '4px',
            cursor: 'pointer',
            border: '1px solid #ccc',
        },
    };

    return (
        <div style={styles.container}>
            <h1 style={styles.heading}>Add Question</h1>

            {/* Success and error messages */}
            {successMessage && <div style={{ ...styles.message, ...styles.successMessage }}>{successMessage}</div>}
            {error && <div style={{ ...styles.message, ...styles.errorMessage }}>{error}</div>}

            {/* Question Form */}
            <div style={styles.formGroup}>
                <label style={styles.label}>Question</label>
                <input
                    type="text"
                    style={styles.input}
                    value={question}
                    onChange={(e) => setQuestion(e.target.value)}
                />
            </div>

            {options.map((option, index) => (
                <div key={index} style={styles.formGroup}>
                    <label style={styles.label}>Option {index + 1}</label>
                    <input
                        type="text"
                        style={styles.input}
                        value={option}
                        onChange={(e) => handleOptionChange(index, e.target.value)}
                    />
                </div>
            ))}

            <div style={styles.formGroup}>
                <label style={styles.label}>Correct Option</label>
                <input
                    type="text"
                    style={styles.input}
                    value={correctOption}
                    onChange={(e) => setCorrectOption(e.target.value)}
                />
            </div>

            <div style={styles.formGroup}>
                <label style={styles.label}>Mark</label>
                <input
                    type="number"
                    style={styles.input}
                    value={mark}
                    onChange={(e) => setMark(e.target.value)}
                />
            </div>

            <div style={styles.formGroup}>
                <label style={styles.label}>Negative Mark</label>
                <input
                    type="number"
                    style={styles.input}
                    value={negativeMark}
                    onChange={(e) => setNegativeMark(e.target.value)}
                />
            </div>

            <button
                style={{ ...styles.button, ...styles.primaryButton }}
                onClick={handleSubmit}
                disabled={loading}
            >
                {loading ? 'Submitting...' : 'Submit'}
            </button>
            <button
                style={{ ...styles.button, ...styles.secondaryButton }}
                onClick={handleNext}
            >
                Add Next Question
            </button>

            {/* Grid for displaying questions */}
            <div style={styles.gridContainer}>
                {questionsList.map((q, index) => (
                    <div
                        key={index}
                        style={styles.gridTile}
                        onClick={() => handleGridTileClick(index)}
                    >
                        Question {index + 1}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default AddQuestion;
