import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/EditQuestion.css';

const EditQuestion = () => {
  const navigate = useNavigate();
  const [questions, setQuestions] = useState([]); // Store all questions
  const [newQuestions, setNewQuestions] = useState([]); // Store newly added questions
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeQuestion, setActiveQuestion] = useState(null); // For displaying selected question details

  // Fetch all questions when the component mounts
  useEffect(() => {
    fetch('http://localhost:8000/staff/editquestion/') // Full URL to get all questions
      .then(response => response.json())
      .then(data => {
        setQuestions(data);
        // Filter the new questions based on a condition (e.g., ID, or specific flag)
        const addedQuestions = data.filter(question => question.isNew); // Example: filter for newly added questions
        setNewQuestions(addedQuestions);
        setLoading(false);
      })
      .catch(err => {
        setError('Error fetching question data');
        setLoading(false);
      });
  }, []);

  // Handle input changes for each question
  const handleChange = (e, index) => {
    const { name, value } = e.target;
    const updatedQuestions = [...questions];

    if (name === 'options') {
      updatedQuestions[index][name] = value.split(',').map(option => option.trim()); // Convert to array of options
    } else {
      updatedQuestions[index][name] = value;
    }

    setQuestions(updatedQuestions);
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // Send the updated question data to the Django API
    fetch('http://localhost:8000/staff/editquestion/', {  // Full URL to update questions
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(questions),  // Send all questions
    })
      .then(response => response.json())
      .then(data => {
        // On success, redirect back to the question list or another page
        navigate('/staffhome');
      })
      .catch(err => {
        setError('Error updating questions');
      });
  };

  // Handle selection of a question to display its details
  const handleSelectQuestion = (question) => {
    setActiveQuestion(question);
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="edit-question-container">
      <h1>Edit Questions</h1>
      
      {activeQuestion && (
        <div className="selectedQuestion">
          <h3>Selected Question Details:</h3>
          <p><strong>Question:</strong> {activeQuestion.question_text}</p>
          <p><strong>Options:</strong> {activeQuestion.options.join(', ')}</p>
          <p><strong>Correct Option:</strong> {activeQuestion.correct_option}</p>
          <p><strong>Mark:</strong> {activeQuestion.mark}</p>
          <p><strong>Negative Mark:</strong> {activeQuestion.negative_mark}</p>
        </div>
      )}

      {/* Display only newly added questions */}
      {newQuestions.length > 0 && (
        <div className="new-questions-container">
          <h3>New Questions Added:</h3>
          <ul>
            {newQuestions.map((question, index) => (
              <li key={index} className="question-item">
                <h4>{question.question_text}</h4>
                <ul className="options-list">
                  {question.options.map((option, idx) => (
                    <li key={idx} className="option-item">{option}</li>
                  ))}
                </ul>
                <p><strong>Correct Option:</strong> {question.correct_option}</p>
                <p><strong>Mark:</strong> {question.mark}</p>
                <p><strong>Negative Mark:</strong> {question.negative_mark}</p>
              </li>
            ))}
          </ul>
        </div>
      )}

      <form onSubmit={handleSubmit}>
        {questions.map((question, index) => (
          <div key={question.id} className="question-form">
            <h2>Question {index + 1}</h2>

            <div className="form-group">
              <label>Question Text:</label>
              <input
                type="text"
                name="question_text"
                value={question.question_text}
                onChange={(e) => handleChange(e, index)}
              />
            </div>

            <div className="form-group">
              <label>Options (comma separated):</label>
              <input
                type="text"
                name="options"
                value={question.options.join(', ')}
                onChange={(e) => handleChange(e, index)}
              />
            </div>

            <div className="form-group">
              <label>Correct Option:</label>
              <input
                type="text"
                name="correct_option"
                value={question.correct_option}
                onChange={(e) => handleChange(e, index)}
              />
            </div>

            <div className="form-group">
              <label>Mark:</label>
              <input
                type="number"
                name="mark"
                value={question.mark}
                onChange={(e) => handleChange(e, index)}
              />
            </div>

            <div className="form-group">
              <label>Negative Mark:</label>
              <input
                type="number"
                name="negative_mark"
                value={question.negative_mark}
                onChange={(e) => handleChange(e, index)}
              />
            </div>

            <button type="button" onClick={() => handleSelectQuestion(question)}>
              View Question Details
            </button>
          </div>
        ))}

        <button type="submit">Save</button>
      </form>
    </div>
  );
};

export default EditQuestion;
