import React, { useState } from 'react';
import '../App.css';

const EditQuestion = () => {
    const [question, setQuestion] = useState('');
    const [options, setOptions] = useState(['', '', '']);
    const [mark, setMark] = useState('');
    const [negativeMark, setNegativeMark] = useState('');

    const handleOptionChange = (index, value) => {
        const newOptions = [...options];
        newOptions[index] = value;
        setOptions(newOptions);
    };

    const addOption = () => {
        setOptions([...options, '']);
    };

    const handleSubmit = () => {
        // Handle the form submission logic here
        console.log({
            question,
            options,
            mark,
            negativeMark,
        });
    };

    return (
        <div className="edit-question-container">
            <h1>Add Question</h1>
            <div className="form-group">
                <label>Question*</label>
                <textarea
                    value={question}
                    onChange={(e) => setQuestion(e.target.value)}
                    placeholder="Enter your question here"
                ></textarea>
            </div>

            <div className="form-group">
                <label>Answer*</label>
                <p>Add choices & choose the correct answers</p>
                {options.map((option, index) => (
                    <input
                        key={index}
                        type="text"
                        value={option}
                        onChange={(e) => handleOptionChange(index, e.target.value)}
                        placeholder={`Option ${index + 1}`}
                    />
                ))}
                <button onClick={addOption} className="add-option-btn">Add Option</button>
            </div>

            <div className="form-group">
                <label>Mark</label>
                <input
                    type="text"
                    value={mark}
                    onChange={(e) => setMark(e.target.value)}
                    placeholder="Enter mark for the question"
                />
            </div>

            <div className="form-group">
                <label>Negative Mark</label>
                <input
                    type="text"
                    value={negativeMark}
                    onChange={(e) => setNegativeMark(e.target.value)}
                    placeholder="Enter negative mark"
                />
            </div>

            <button onClick={handleSubmit} className="submit-btn">Next Step</button>
        </div>
    );
};

export default EditQuestion;
