import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const EditQuestion = () => {
  const navigate = useNavigate();
  const [question, setQuestion] = useState({
    question_text: '',
    options: [],
    correct_option: '',
    mark: 0,
    negative_mark: 0,
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Fetch the question data when the component mounts
  useEffect(() => {
    fetch(`http://localhost:8000/staff/editquestion/`)  // No ID in URL
      .then(response => response.json())
      .then(data => {
        setQuestion(data);
        setLoading(false);
      })
      .catch(err => {
        setError('Error fetching question data');
        setLoading(false);
      });
  }, []);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'options') {
      setQuestion((prevState) => ({
        ...prevState,
        [name]: value.split(',').map(option => option.trim()), // Convert input into an array of options
      }));
    } else {
      setQuestion((prevState) => ({
        ...prevState,
        [name]: value,
      }));
    }
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // Send the updated question data to the Django API
    fetch(`http://localhost:8000/staff/editquestion/`, {  // No ID in URL
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(question),
    })
      .then(response => response.json())
      .then(data => {
        // On success, redirect back to the question list or some other page
        navigate('/questions');
      })
      .catch(err => {
        setError('Error updating question');
      });
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Edit Question</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Question Text:</label>
          <input
            type="text"
            name="question_text"
            value={question.question_text}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Options (comma separated):</label>
          <input
            type="text"
            name="options"
            value={question.options.join(', ')}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Correct Option:</label>
          <input
            type="text"
            name="correct_option"
            value={question.correct_option}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Mark:</label>
          <input
            type="number"
            name="mark"
            value={question.mark}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Negative Mark:</label>
          <input
            type="number"
            name="negative_mark"
            value={question.negative_mark}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Save</button>
      </form>
    </div>
  );
};

export default EditQuestion;
